mapper.select:
SELECT R.RENO, R.USERID, R.G_PHONE, G.G_NAME, R.CDATETIME, PD.P_NAME, PD.P_PRICE, R.PRODUCTNO, PM.IMGSRC, R.ORDERNO FROM mini_db2.T1_RETURN R INNER JOIN mini_db2.T1_CARD C ON C.G_PHONE = R.G_PHONE AND C.PRODUCTNO = R.PRODUCTNO INNER JOIN mini_db2.T1_PRODUCT PD ON PD.PRODUCTNO = R.PRODUCTNO INNER JOIN mini_db2.T1_PDIMAGE PM ON PM.PRODUCTNO = PD.PRODUCTNO INNER JOIN (SELECT G_NAME, G_PHONE FROM mini_db2.T1_GUEST GROUP BY G_PHONE) G ON G.G_PHONE = R.G_PHONE WHERE R.USERID = #{userId} AND IMGSRC NOT LIKE '%BackGround%'AND MAINYN = 'Y'
AND G.G_NAME LIKE CONCAT('%', #{keyword}, '%')
LIMIT #{startNum}, 6;
